var yaa = require('../../../bin/index.js');
var test = require('../../test.js');
var cases = [];
var context = {};

module.exports = test(cases, __filename.substr(__dirname.length + 1), context);


var collector = yaa.collector.array();

cases.push(yaa.assert.outputEquals(
    yaa.use(collector, yaa.insert(123)), yaa.nothing(), 'Collector correct.'));

cases.push(yaa.assert.outputEquals(
    yaa.use(collector, yaa.insert(456)), yaa.nothing(), 'Collector correct.'));

cases.push(yaa.assert.outputEquals(
    yaa.use(collector, yaa.insert(789)), yaa.nothing(), 'Collector correct.'));

cases.push(yaa.assert.outputEquals(
    yaa.use(collector, yaa.insert(0)), yaa.nothing(), 'Collector correct.'));

cases.push(yaa.assert.outputDeepEquals(
    yaa.use(collector, yaa.nothing()),
    yaa.insert([123, 456, 789, 0]), 'Collector correct.'));