var yaa = require('../../bin/index.js');
var test = require('../test.js');
var cases = [];
var context = {};

module.exports = test(cases, __filename.substr(__dirname.length + 1), context);


var one = {};
var two = {};


cases.push(yaa.assert.outputEquals(
    yaa.if(yaa.no(), yaa.insert(one)), yaa.nothing(), '`yaa.no()` is false.'));

cases.push(yaa.assert.outputEquals(
    yaa.if(yaa.nothing(), yaa.insert(one)), yaa.nothing(), '`yaa.nothing()` is false.'));

cases.push(yaa.assert.outputEquals(
    yaa.if(yaa.no(), yaa.insert(one), yaa.insert(two)), yaa.insert(two), '`yaa.if` return `else`.'));

cases.push(yaa.assert.outputEquals(
    yaa.if(yaa.yes(), yaa.insert(one)), yaa.insert(one), '`yaa.if` return `then`.'));


cases.push(yaa.if(yaa.assert.contextEquals(context, 'Context 1 correct.'),
    yaa.nop(),
    yaa.assert.contextEquals(context, 'Context 2 correct.')));


cases.push(yaa.if(yaa.yes(),
    yaa.assert.contextEquals(context, 'Context 3 correct.')));