var yaa = require('../../bin/index.js');
var test = require('../test.js');
var cases = [];
var context = {};

module.exports = test(cases, __filename.substr(__dirname.length + 1), context);

var one = {};
var two = {};
var three = {};
var four = {};
var five = {};

cases.push(yaa.use(yaa.assert.contextEquals(context, 'Context is correct.'),
    yaa.assert.contextEquals(context, 'Context is correct.')));

cases.push(yaa.assert.outputEquals(
    yaa.use(yaa.nop(), yaa.insert(one)), yaa.insert(one), 'Join correct.'));

cases.push(yaa.assert.outputEquals(
    yaa.use(yaa.nop(), yaa.insert(one, two)), yaa.insert(one, two), 'Join correct.'));

cases.push(yaa.assert.outputEquals(
    yaa.use(yaa.nop(), yaa.insert(one, two, three)), yaa.insert(one, two, three), 'Join correct.'));

cases.push(yaa.assert.outputEquals(
    yaa.use(yaa.nop(), yaa.insert(one, two, three, four)), yaa.insert(one, two, three, four), 'Join correct.'));

cases.push(yaa.assert.outputEquals(
    yaa.use(yaa.nop(), yaa.insert(one, two, three, four, five)), yaa.insert(one, two, three, four, five), 'Join correct.'));
