


/**
 * @constructor
 * @param {number} type
 * @param {number} id
 * @param {thrift.Value} value
 */
thrift.Field = function(type, id, value) {

  /**
   * @type {thrift.Value}
   */
  this.__value = value;

  /**
   * @type {number}
   */
  this.__type = type;

  /**
   * @type {number}
   */
  this.__id = id;
};


/**
 * @return {thrift.Value}
 */
thrift.Field.prototype.getValue = function() {
  return this.__value;
};


/**
 * @return {number}
 */
thrift.Field.prototype.getType = function() {
  return this.__type;
};


/**
 * @return {number}
 */
thrift.Field.prototype.getId = function() {
  return this.__id;
};
